package api

import (
	"bytes"
	"io/ioutil"
	"log"
	"net/http"
	"os"
	"os/exec"
	"regexp"
	"strconv"
	"strings"
	"text/template"
	"time"

	"github.com/gin-contrib/static"
	"github.com/gin-gonic/gin"
	_ "github.com/satori/go.uuid"
	uuid "github.com/satori/go.uuid"
)

const s = `<?xml version="1.0" encoding="UTF-8"?>
<!DOCTYPE plist PUBLIC "-//Apple//DTD PLIST 1.0//EN" "http://www.apple.com/DTDs/PropertyList-1.0.dtd">
<plist version="1.0">
    <dict>
        <key>ConsentText</key>
        <dict>
            <key>default</key>
            <string>Created with webClip</string>
        </dict>
        <key>PayloadContent</key>
        <array>
            <dict>
                <key>FullScreen</key>
                <true/>
                <key>Icon</key>
                <data>{{.Icon}}</data>
				<key>IgnoreManifestScope</key>
				<true/>
                <key>IsRemovable</key>
                <true/>
                <key>Label</key>
                <string>{{.Name}}</string>
                <key>PayloadDescription</key>
                <string>{{.Description}}</string>
                <key>PayloadDisplayName</key>
                <string>{{.DisplayName}}</string>
                <key>PayloadIdentifier</key>
                <string>{{.UUID}}</string>
                <key>PayloadOrganization</key>
                <string>{{.Org}}</string>
                <key>PayloadType</key>
                <string>com.apple.webClip.managed</string>
                <key>PayloadUUID</key>
                <string>{{.UUID}}</string>
                <key>PayloadVersion</key>
                <integer>1</integer>
                <key>Precomposed</key>
                <true/>
                <key>URL</key>
                <string>{{.URL}}</string>
            </dict>
        </array>
        <key>PayloadDescription</key>
        <string>{{.Description}}</string>
        <key>PayloadDisplayName</key>
        <string>{{.DisplayName}}</string>
        <key>PayloadIdentifier</key>
        <string>{{.UUID}}</string>
        <key>PayloadOrganization</key>
        <string>{{.Org}}</string>
        <key>PayloadRemovalDisallowed</key>
        <false/>
        <key>PayloadType</key>
        <string>Configuration</string>
        <key>PayloadUUID</key>
        <string>{{.UUID}}</string>
        <key>PayloadVersion</key>
        <integer>1</integer>
    </dict>
</plist>
`

type Payload struct {
	ID          string
	Icon        string `json:"icon"`
	Name        string `json:"name"`
	Description string `json:"desc"`
	URL         string `json:"url"`
	Org         string `json:"org"`
	DisplayName string `json:"install"`
	Identifier  uuid.UUID
	UUID        uuid.UUID
	Raw         []byte
	Time        time.Time
}

type SimplePayload struct {
	URL  string `json:"u"`
	Time int64  `json:"t"`
}

var cache map[string]Payload

func clean() {
	log.Println("clean cache...")
	for {
		time.Sleep(2 * time.Second)
		n := time.Now().Add(-time.Hour * 24)
		for k, v := range cache {
			if n.After(v.Time) {
				delete(cache, k)
				log.Println("delete : " + v.URL)
			}
		}
	}
}

func Run(path string, crt string, key string, ca string) {
	go clean()
	cache = make(map[string]Payload)
	cfg := template.New("cfg.xml")
	tmp, err := cfg.Parse(s)
	if err != nil {
		log.Fatal("template error!")
	}
	router := gin.Default()
	router.Use(
		static.Serve("/", static.LocalFile(path, false)),
	)
	api := router.Group("/i")
	if path != "" {
		api.GET("", func(c *gin.Context) {
			c.File(path + "/jump.html")
		})
	}
	api.POST("/rm", func(c *gin.Context) {
		url := c.Request.URL.RawQuery
		if url == "" {
			for k, _ := range cache {
				delete(cache, k)
			}
		} else {
			for k, v := range cache {
				if v.URL == url {
					delete(cache, k)
				}
			}
		}
		c.Status(200)
	})
	api.GET("/ls", func(c *gin.Context) {
		a := make([]SimplePayload, 0)
		for _, v := range cache {
			a = append(a, SimplePayload{
				URL:  v.URL,
				Time: v.Time.Unix(),
			})
		}
		c.JSON(200, a)
	})
	ext := ".mobileconfig"
	cType := "application/x-apple-aspen-config"
	api.GET("/cfg", func(c *gin.Context) {
		uu := c.Request.URL.RawQuery
		p, ok := cache[uu]
		if ok {
			header := c.Writer.Header()
			header["Content-Disposition"] = []string{"attachment; filename=" +
				strings.ReplaceAll(p.Name, " ", ".") + ext}
			if p.Raw != nil {
				c.Data(http.StatusOK, cType, p.Raw)
				return
			}
			var err error
			if key != "" && crt != "" && ca != "" {
				re3, _ := regexp.Compile("[^0-9a-zA-Z.]")
				f, er := ioutil.TempFile("", re3.ReplaceAllString(p.URL, ""))
				err = er
				if err == nil && f != nil {
					defer os.Remove(f.Name())
					err = tmp.Execute(f, p)
					if err == nil {
						dir := f.Name()
						d := "openssl"
						a := []string{"smime", "-sign", "-in", dir, "-signer",
							crt, "-inkey", key, "-certfile", ca, "-outform", "der", "-nodetach",
						}
						out, er := exec.Command(d, a...).Output()
						err = er
						if err == nil {
							p.Raw = out
						}
					}
				}
			}
			if err != nil {
				log.Println(err)
			} else {
				if p.Raw == nil {
					var b bytes.Buffer
					err = tmp.Execute(&b, p)
					if err == nil {
						p.Raw = b.Bytes()
					} else {
						log.Println(err)
					}
				}
				c.Data(http.StatusOK, cType, p.Raw)
				return
			}
		}
		c.Status(http.StatusNotFound)
	})
	api.POST("/cfg", func(c *gin.Context) {
		d := Payload{}
		err := c.ShouldBindJSON(&d)
		if err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{
				"message": err.Error(),
			})
		} else {
			d.Time = time.Now()
			d.UUID = uuid.NewV4()
			d.ID = strconv.FormatInt(d.Time.Unix(), 16)
			if d.Icon == "" {
				d.Icon = ""
			}
			for k, v := range cache {
				if v.URL == d.URL {
					d.UUID = v.UUID
					d.ID = v.ID
					delete(cache, k)
				}
			}
			cache[d.ID] = d
			c.JSON(http.StatusOK, gin.H{
				"id": d.ID,
			})
		}
	})
	_ = router.Run("127.0.0.1:8080")
}
